import { Component, Input, OnInit } from '@angular/core';
import { Produits } from '../models/produits.model';
import { ProduitService } from '../services/produits.services';
import { ActivatedRoute } from '@angular/router';
import { Observable } from 'rxjs';
import { Photos } from '../models/photos.model';

@Component({
  selector: 'app-produits',
  templateUrl: './produits.component.html',
  styleUrls: ['./produits.component.scss']
})
export class ProduitsComponent implements OnInit{

  @Input() produits!: Produits
  photos$!: Observable<Photos[]>;


  constructor(private produitService : ProduitService,
              private route: ActivatedRoute){}

  ngOnInit(): void {
    this.photos$ = this.produitService.getAllPhotoByRef(this.produits.id);
 console.log(this.photos$)
}
}

 
    <div class=" border p-3 m-2 d-flex flex-row flex-nowrap">
               <div *ngFor="let photo of photos$ | async">
                <div *ngIf="photo.isPrimary === true">
  <img  [src]="photo.src" alt="Photo" style="max-height: 300px;">
                 
          
</div> 

<div class="col-8">
             <h3>{{ produits.ShortLibel }}</h3>


    
              
        </div>
     <div class="col-4 ">
              <button class="btn-modify border-none bg-Light btn btn-success m-1" ><i class="fa-solid fa-pen-to-square"></i></button>
      <button class="btn-delete btn btn-danger" ><i class="fa-solid fa-trash"></i></button>
      <button class="btn-details btn btn-primary m-1" ><i class="fa-solid fa-circle-plus"></i></button>
     </div>



    </div>
