import { Injectable } from "@angular/core";
import { Categories } from "../models/categories.model";
import { HttpClient } from "@angular/common/http";
import { Observable } from "rxjs";
import { filter, map } from 'rxjs/operators';

@Injectable({
    providedIn: 'root'
})

export class CategorieService {

    constructor(private http: HttpClient) {}

    getAllCategories(): Observable<Categories[]> {
        return this.http.get<Categories[]>('https://diane.amorce.org/api/categories').pipe(
            switchMap((categories: Categories[]) => {
                const parentCategories = categories.filter(category => category.parentId === null);
                const subCategoriesRequests = parentCategories.map(category => this.getSubCategories(category.id, categories));
                return forkJoin(subCategoriesRequests).pipe(
                    map(subCategories => {
                        parentCategories.forEach((category, index) => {
                            category.subCategories = subCategories[index];
                        });
                        return parentCategories;
                    })
                );
            })
        );
    }
    
    getSubCategories(parentId: number, categories: Categories[]): Observable<Categories[]> {
        return of(categories.filter(category => category.parentId === parentId));
    }
}
