import { Component, OnInit } from '@angular/core';
import { Produits } from '../models/produits.model';
import { ProduitService } from '../services/produits.services';
import { Observable, map } from 'rxjs';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-produits-list',
  templateUrl: './produits-list.component.html',
  styleUrls: ['./produits-list.component.scss']
})
export class ProduitsListComponent implements OnInit {

  produits$!: Observable<Produits[]>


  constructor(
    private route: ActivatedRoute,
    private produitService: ProduitService){}


    ngOnInit(): void {
      const categoryId = +this.route.snapshot.params['id'];

  this.produits$ = this.produitService.getAllProduits().pipe(
    map(produits => produits.reduce((acc, produit) => {
      if (this.extractCategoryIdFromUrl(produit.categorie.toString()) === categoryId) {
        acc.push(produit);
      }
      return acc;
    }, [] as Produits[]))
  );
}
    private extractCategoryIdFromUrl(url: string): number {
      const segments = url.split('/');
      const categoryIdSegment = segments[segments.length - 1];
      return +categoryIdSegment;
    }

}
