import { Injectable } from "@angular/core";
import { Categories } from "../models/categories.model";
import { HttpClient } from "@angular/common/http";
import { Observable } from "rxjs";
import { map } from 'rxjs/operators';

@Injectable({
    providedIn: 'root'
})
export class CategorieService {
    constructor(private http: HttpClient) {}

    getAllCategories(): Observable<Categories[]> {
        return this.http.get<Categories[]>('https://diane.amorce.org/api/categories').pipe(
            map((categories: Categories[]) => {
                categories.forEach(category => {
                    category.subCategories = categories.filter(c => c.parentId === category.id);
                });
                return categories;
            })
        );
    }

    getCategorieById(id: number): void {
        const categorie = this.categoriesList.find(categorie => categorie.id === id);
        if (!categorie) {
            throw new Error('categorie not found');
        }
    }
}
