import { Component, OnInit } from '@angular/core';
import { ProduitService } from '../services/produits.services';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Observable, map } from 'rxjs';
import { Produits } from '../models/produits.model';
import { ActivatedRoute } from '@angular/router';
import { CategorieService } from '../services/categories.services';

@Component({
  selector: 'app-new-produit',
  templateUrl: './new-produit.component.html',
  styleUrls: ['./new-produit.component.scss']
})
export class NewProduitComponent implements OnInit{

  createForm!:FormGroup;
  produitPreview$!: Observable<Produits>;
  currentCategoryId!: number | null;

  constructor(private produitService: ProduitService,
    private route : ActivatedRoute,
    private categoryService: CategorieService,
    private formBuilder: FormBuilder) {}

    ngOnInit(): void {
      this.createForm = this.formBuilder.group({
        ShortLibel: [null],
         LongLibel: [null],
         prxHt: [null],
         photos:[null]
      });

    // Récupérez l'ID de la catégorie depuis le service
    this.currentCategoryId = this.categoryService.getCurrentCategoryId();
    

    this.produitPreview$ = this.createForm.valueChanges.pipe(
        map(formvalue => ({ 
          ...formvalue,
          slug: slugify(formvalue.ShortLibel),
          categorie: this.currentCategoryId,
        }))
      )
    
  }
  onSubmitForm(): void {
    console.log(this.createForm.value);
}
  addPhoto():void {
    console
  }
}

function slugify(text: string): string {
  const normalizedText = text.replace(/[^a-zA-Z0-9\s]/g, "").toLowerCase();
  const slug = normalizedText.replace(/\s+/g, "-");
  return slug;
}

<div class="container mt-5">
  <div class="row">
    <div class="col-md-6 border pb-5">
      <div class="container mt-3" *ngIf="produitPreview$ | async as produit">
        <h2 class="mb-3">{{ produit.ShortLibel }}</h2>
        <h5 class="mb-3">{{ produit.slug }}</h5>
        <p class="mb-3">{{ produit.LongLibel }}</p>
        <p class="mb-3 display-6">{{ produit.prxHt }} €</p>
        <div class="d-flex flex-row flex-wrap">
          <div>
            <img
              [src]="produit.photos"
              alt="Photo"
              style="max-height: 120px"
              class="me-2 mb-2"
            />
          </div>
        </div>
      </div>
    </div>

    <div class="col-md-6">
      <!-- Formulaire de modification -->
      <form [formGroup]="createForm">
        <div class="mb-3">
          <label for="ShortLibel" class="form-label">Nom du produit</label>
          <input
            type="text"
            class="form-control"
            id="ShortLibel"
            formControlName="ShortLibel"
            name="ShortLibel"
          />
        </div>

        <div class="mb-3">
          <label for="LongLibel" class="form-label"
            >Description du produit</label
          >
          <textarea
            type="text"
            class="form-control"
            name="LongLibel"
            formControlName="LongLibel"
            id="LongLibel"
          ></textarea>
        </div>
        <div class="mb-3">
          <label for="prxHt" class="form-label">Prix du produit</label>
          <input
            type="text"
            class="form-control"
            id="prxHt"
            name="prxHt"
            formControlName="prxHt"
            id="prxHt"
          />
        </div>
        <div class="mb-3 flex-row d-flex">
          <label for="prxHt" class="form-label">photos</label>       <button type="button" class="btn btn-primary" (click)="addPhoto()">
              Ajouter une photo
            </button>
        </div>

    

        <!-- Autres champs de modification -->
        <button type="submit" class="btn btn-primary" (click)="onSubmitForm()">
          Créer un produit
        </button>
      </form>
    </div>
  </div>
</div>
