import { Injectable } from "@angular/core";
import { Categories } from "../models/categories.model";
import { HttpClient } from "@angular/common/http";
import { Observable } from "rxjs";
import { filter, map } from 'rxjs/operators';

@Injectable({
    providedIn: 'root'
})

export class CategorieService {

    constructor(private http: HttpClient) {}

    getAllCategories(): Observable<Categories[]> {
        return this.http.get<Categories[]>('https://diane.amorce.org/api/categories').pipe(
            map((categories: Categories[]) => {
                categories.forEach(category => {
                    category.subCategories = this.getSubCategories(category.id, categories);
                });
                return categories.filter(category => category.parentId === null);
            })
        );
    }

    getSubCategories(parentId: number, categories: Categories[]): Observable<Categories[]> {
        return this.http.get<Categories[]>('https://diane.amorce.org/api/categories').pipe(
            map((allCategories: Categories[]) => {
                return allCategories.filter(category => category.parentId === parentId);
            })
        );
    }
}
