import { Component, OnInit } from '@angular/core';
import { Produits } from '../models/produits.model';
import { ProduitService } from '../services/produits.services';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-produits-list',
  templateUrl: './produits-list.component.html',
  styleUrls: ['./produits-list.component.scss']
})
export class ProduitsListComponent implements OnInit {

  produits$!: Observable<Produits[]>
  produits!: Produi[];
  photosData!: any[];

  constructor(private produitService: ProduitService){}

  ngOnInit(): void {
    this.produitService.getAllProduits().subscribe(produits => {
      this.produit = produits;

      const photoUrls = produits.reduce(
        (urls, produit) => urls.concat(produit.photos),
        []
      );

      this.produitService.getPhotosData(photoUrls).subscribe(photosData => {
        this.photosData = photosData;
      });
    });
  }
}
