import { Component, Input, OnInit } from '@angular/core';
import { Categories } from '../models/categories.model';

@Component({
  selector: 'app-categories',
  templateUrl: './categories.component.html',
  styleUrls: ['./categories.component.scss']
})

export class CategoriesComponent implements OnInit{

  @Input() categories!: Categories

  ngOnInit(): void {

  }
}
<div *ngFor="let category of categories">
  <div *ngIf="category.parentId === null" class="card mt-2" style="width: 18rem;">
    <button class="card-body btn">
      <h5 class="card-title">{{ category.nomination }}</h5>
      <p class="card-subtitle mb-2 text-muted">See all product from {{ category.slug }}</p>
    </button>
  </div>
</div>
