import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-dynamic-field',
  template: `
    <div class="mb-3">
      <label for="input{{ index }}" class="form-label">Div {{ index + 1 }}</label>
      <input type="text" class="form-control" id="input{{ index }}" name="input{{ index }}" [(ngModel)]="content">
      <button type="button" class="btn btn-danger" (click)="onDeleteClick()">Supprimer</button>
    </div>
  `
})
export class DynamicFieldComponent {
  @Input() content: string = '';
  @Output() onDelete: EventEmitter<void> = new EventEmitter<void>();
  @Input() index: number = 0;

  onDeleteClick() {
    this.onDelete.emit();
  }
}
